import streamlit as st

st.set_page_config(
    page_title="Aplikasi Perhitungan Luas Bangun Datar",page_icon="",)
st.title('Aplikasi Bangun Datar')
st.write("Silahkan pilih menu di samping untuk memulai")

def persegi(sisi):
    return sisi * sisi

def segitiga(alas, tinggi):
    return 0.5 * alas * tinggi

def lingkaran(jari_jari):
    return 3.14 * jari_jari * jari_jari

menu=st.sidebar.selectbox('Pilih bangun datar', ['Luas Persegi', 'Luas Segitiga', 'Luas Lingkaran'])

if menu == 'Luas Persegi':
    st.header('Luas Persegi')

    st.image('https://www.doyanblog.com/wp-content/uploads/2021/12/rumus-persegi.jpg.webp', caption="rimus luas persegi",width=300)

    sisi = st.number_input('Masukkan panjang sisi (cm):', min_value=0)
    luas = persegi(sisi)
    st.write(f"Luas Persegi: {luas} cm²")

elif menu == 'Luas Segitiga':
    st.header('Luas Segitiga')

    st.image('data:image/png;base64,', caption="rumus luas segitiga",width=300)

    alas = st.number_input('Masukkan panjang alas (cm):', min_value=0)
    tinggi = st.number_input('Masukkan tinggi (cm):', min_value=0)
    if st.button('Hitung Luas Segitiga'):
        luas = segitiga(alas, tinggi)
        st.write(f"Luas Segitiga: {luas} cm²")

elif menu == 'Luas Lingkaran':
    st.header('Luas Lingkaran')

    st.image('data:image/png;base64,', caption="rumus luas lingkaran",width=300)

    jari_jari = st.number_input('Masukkan panjang jari-jari (cm):', min_value=0)
    if st.button('Hitung Luas Lingkaran'):
        luas = lingkaran(jari_jari)
        st.write(f"Luas Lingkaran: {luas} cm²")

